import axios from 'axios';
import { User, LeaderboardEntry, ClaimResponse } from './types';

const API_URL = 'http://localhost:5000/api';

export const api = {
  // Get all users
  getUsers: async (): Promise<User[]> => {
    const response = await axios.get(`${API_URL}/users`);
    return response.data;
  },

  // Add new user
  addUser: async (name: string): Promise<User> => {
    const response = await axios.post(`${API_URL}/users`, { name });
    return response.data;
  },

  // Claim points for a user
  claimPoints: async (userId: string): Promise<ClaimResponse> => {
    const response = await axios.post(`${API_URL}/claim`, { userId });
    return response.data;
  },

  // Get leaderboard
  getLeaderboard: async (): Promise<LeaderboardEntry[]> => {
    const response = await axios.get(`${API_URL}/leaderboard`);
    return response.data;
  }
};
