import React, { useState, useEffect } from 'react';
import { User } from '../types';
import { api } from '../api';

interface UserListProps {
  onUserSelect: (userId: string) => void;
  selectedUserId: string | null;
}

export const UserList: React.FC<UserListProps> = ({ onUserSelect, selectedUserId }) => {
  const [users, setUsers] = useState<User[]>([]);
  const [newUserName, setNewUserName] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    loadUsers();
  }, []);

  const loadUsers = async () => {
    try {
      setError(null);
      const data = await api.getUsers();
      setUsers(data);
    } catch (error) {
      setError(error instanceof Error ? error.message : 'Failed to load users');
    }
  };

  const handleAddUser = async (e: React.FormEvent) => {
    e.preventDefault();
    const trimmedName = newUserName.trim();
    
    if (!trimmedName) {
      setError('Please enter a user name');
      return;
    }

    try {
      setIsLoading(true);
      setError(null);
      const newUser = await api.addUser(trimmedName);
      setUsers(prevUsers => [...prevUsers, newUser]);
      setNewUserName('');
    } catch (error) {
      setError(error instanceof Error ? error.message : 'Failed to add user');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="user-list">
      <h2>Users</h2>
      
      <form onSubmit={handleAddUser} className="add-user-form">
        <input
          type="text"
          value={newUserName}
          onChange={(e) => {
            setNewUserName(e.target.value);
            setError(null);
          }}
          placeholder="Enter user name"
          className="user-input"
          disabled={isLoading}
          minLength={2}
          maxLength={50}
        />
        <button 
          type="submit" 
          className="add-button"
          disabled={isLoading || !newUserName.trim()}
        >
          {isLoading ? 'Adding...' : 'Add User'}
        </button>
      </form>

      {error && <div className="error-message">{error}</div>}

      <div className="users-container">
        {users.length === 0 ? (
          <div className="empty-message">No users yet. Add some users to get started!</div>
        ) : (
          users.map((user) => (
            <div
              key={user._id}
              className={`user-item ${selectedUserId === user._id ? 'selected' : ''}`}
              onClick={() => onUserSelect(user._id)}
            >
              <span className="user-name">{user.name}</span>
              <span className="user-points">{user.totalPoints} points</span>
            </div>
          ))
        )}
      </div>
    </div>
  );
};
