import axios, { AxiosError } from 'axios';
import { User, LeaderboardEntry, ClaimResponse } from './types';

// Function to test server connection
const findServerPort = async () => {
  const ports = [5000, 5001, 5002, 5003, 5004, 5005];
  
  for (const port of ports) {
    try {
      const response = await axios.get(`http://localhost:${port}/api/users`);
      if (response.status === 200) {
        return port;
      }
    } catch (error) {
      const axiosError = error as AxiosError;
      if (axiosError.code !== 'ECONNREFUSED') {
        continue;
      }
    }
  }
  throw new Error('Could not connect to server on any port');
};

let API_URL = 'http://localhost:5000/api';

// Initialize API URL
findServerPort().then(port => {
  API_URL = `http://localhost:${port}/api`;
  console.log(`Connected to server on port ${port}`);
}).catch(err => {
  console.error('Failed to connect to server:', err);
});

export const api = {
  // Get all users
  getUsers: async (): Promise<User[]> => {
    const response = await axios.get(`${API_URL}/users`);
    return response.data;
  },

  // Add new user
  addUser: async (name: string): Promise<User> => {
    const response = await axios.post(`${API_URL}/users`, { name });
    return response.data;
  },

  // Claim points for a user
  claimPoints: async (userId: string): Promise<ClaimResponse> => {
    const response = await axios.post(`${API_URL}/claim`, { userId });
    return response.data;
  },

  // Get leaderboard
  getLeaderboard: async (): Promise<LeaderboardEntry[]> => {
    const response = await axios.get(`${API_URL}/leaderboard`);
    return response.data;
  }
};
