import React, { useState, useEffect } from 'react';
import { api } from '../api';
import { ClaimHistoryEntry } from '../types';

interface ClaimHistoryProps {
  userId?: string;
  refreshTrigger?: number;
}

export const ClaimHistory: React.FC<ClaimHistoryProps> = ({ userId, refreshTrigger = 0 }) => {
  const [history, setHistory] = useState<ClaimHistoryEntry[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadHistory();
  }, [userId, refreshTrigger]);

  const loadHistory = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await api.getClaimHistory(userId);
      setHistory(data);
    } catch (error) {
      setError('Failed to load claim history');
      console.error('Error loading claim history:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="claim-history">
        <h3>Claim History</h3>
        <div className="loading">Loading history...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="claim-history">
        <h3>Claim History</h3>
        <div className="error">{error}</div>
      </div>
    );
  }

  if (history.length === 0) {
    return (
      <div className="claim-history">
        <h3>Claim History</h3>
        <div className="empty">No points claimed yet</div>
      </div>
    );
  }

  return (
    <div className="claim-history">
      <h3>
        {userId ? 'User Claims' : 'All Claims'}
        <span className="claim-count">({history.length})</span>
      </h3>
      <div className="history-list">
        {history.map((entry) => (
          <div key={entry._id} className="history-item">
            <div className="history-user">{entry.userId.name}</div>
            <div className="history-points">+{entry.points} points</div>
            <div className="history-time">
              {new Date(entry.timestamp).toLocaleString()}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
